import React from 'react';

function emailValidation(email) {
  let result = false;
  if (/^[a-zA-Z0-9.!#$%&'*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*$/.test(email)) {
    result = true;
  }
  return result;
}

function passwordValidation(password) {
  let result = false;
  const parameterNumber = 6;
  if (password.lenght >= parameterNumber) {
    result = true;
  }
  return result;
}

class Login extends React.Component {
  constructor() {
    super();
    this.state = {
      email: '',
      password: '',
      disabled: true,
    };
    this.handleChange = this.handleChange.bind(this);
    // this.buttonValidation = this.buttonValidation.bind(this);
  }

  // buttonValidation() {
  //   const { email } = this.state;
  //   const { password } = this.state;
  //   if (/^[a-zA-Z0-9.!#$%&'*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*$/.test(email) && password.lenght === 6) {
  //     this.setState({
  //       disabled: false,
  //     });
  //   } else {
  //     this.setState({
  //       disabled: true,
  //     });
  //   }
  //   console.log("estou sendo chamada");
  // }

  handleChange(event) {
    const { target } = event;
    const { name, value } = target;
    this.setState({
      [name]: [value],
    });
    const { email } = this.state;
    const { password } = this.state;
    if (emailValidation(email)) {
      this.setState({
        disabled: false,
      });
    }
  }

  render() {
    return (
      <div>
        <form>
          <label htmlFor="email">Email
          <div>
            <input
              name="email"
              data-testid="email-input"
              placeholder="Digite seu email"
              id="email"
              type="text"
              value={this.state.email}
              onChange={this.handleChange}
            />
          </div>
          </label>{' '}
          <label htmlFor="password">Senha</label>{" "}
          <div>
            <input
              name="password"
              data-testid="password-input"
              placeholder="Digite sua senha"
              id="password"
              type="text"
              value={this.state.password}
              onChange={this.handleChange}
            />
          </div>
          <button type="button" disabled={ this.state.disabled }>Entrar</button>
        </form>
      </div>
    );
  }
}

export default Login;
